import resources_rc


class main(QtWidgets.QMainWindow):
    def __init__(self):
        super(QMainWindow, self).__init__()
        self.ui = Ui_MainWindow()
        self.ui.setupUi(self)
        self.setWindowFlags(QtCore.Qt.FramelessWindowHint)
        self.setAttribute(QtCore.Qt.WA_TranslucentBackground)
        self.ui.Full_screen_pushButton_2.hide()
        self.ui.LoginpushButton.clicked.connect(self.login_d)
        self.ui.return_pushButton.clicked.connect(lambda: self.ui.stackedWidget.setCurrentIndex(0))
        self.ui.logon_account_pushButton.clicked.connect(lambda: self.ui.stackedWidget.setCurrentIndex(1))
        self.show()

    # 拖动
    def mousePressEvent(self, event):
        if event.button() == QtCore.Qt.LeftButton and self.isMaximized() == False:
            self.m_flag = True
            self.m_Position = event.globalPos() - self.pos()  # 获取鼠标相对窗口的位置
            event.accept()
            self.setCursor(QtGui.QCursor(QtCore.Qt.OpenHandCursor))  # 更改鼠标图标

    def mouseMoveEvent(self, mouse_event):
        if QtCore.Qt.LeftButton and self.m_flag:
            self.move(mouse_event.globalPos() - self.m_Position)  # 更改窗口位置
            mouse_event.accept()

    def mouseReleaseEvent(self, mouse_event):
        self.m_flag = False
        self.setCursor(QtGui.QCursor(QtCore.Qt.ArrowCursor))

    def login_d(self):
        sql = "SELECT * FROM user"
        name = DataB.Db_R(sql)
        name_tmp = self.ui.Login_Account_lineEdit.text()
        passwd_tmp = self.ui.Login_Account_lineEdit_2.text()
        for i in range(len(name)):
                if name["用户名"][i] == name_tmp and name["密码"][i] == passwd_tmp:
                    self.win = Tr.main()
                    self.close()
                else:
                    pass
